request.query:

fragment HeroFragment on HeroRecord {
    id
    title
    description
    image {
        url
    }
    link {
        label
        externalLink
        page {
            slug
        }
    }
}


fragment TextAndImageFragment on TextAndImageRecord {
    id
    title
    text
    reverse
    image {
        url
    }
    link {
        label
        externalLink
        page {
            slug
        }
    }
}


fragment TextSectionFragment on TextSectionRecord {
    id
    title
    text
    textAlign
}


fragment ProductGroupFragment on ProductGroupRecord {
    id
    title
    product {
        id
        title
        description
        image {
            url
        }
        link {
            slug
        }
    }
}


fragment GalleryFragment on GalleryRecord {
    id
    title
    description
    images {
        url
        id
    }
}


fragment ContactFormFragment on ContactFormRecord {
    id
    title
    image {
        url
    }
}


query Page($slug: String!) {
    page(filter: { slug: { eq: $slug } }) {
        title
        slug
        sections {
            __typename
            ... on TextAndImageRecord {
                ...TextAndImageFragment
            }
            ... on HeroRecord {
                ...HeroFragment
            }
            ... on TextSectionRecord {
                ...TextSectionFragment
            }
            ... on ProductGroupRecord {
                ...ProductGroupFragment
            }
            ... on GalleryRecord {
                ...GalleryFragment
            }
            ... on ContactFormRecord {
                ...ContactFormFragment
            }
        }
    }
    logo {
        image {
            url
        }
    }
    menu {
        links {
            page {
                slug
                title
            }
        }
    }
}
